function uiCheckCtrl(){$(document).on("click","input.radio, input.check",function(){var t=$(this);t.is(".check")?t.prev().toggleClass("active"):t.is(".radio")&&$('input[name="'+t.attr("name")+'"]').each(function(){this==t[0]?$(this).parent().addClass("active"):$(this).parent().removeClass("active")})}).on("focus","input.radio, input.check",function(){$(this).parent().addClass("focus")}).on("blur","input.radio, input.check",function(){$(this).parent().removeClass("focus")}),$that=null}$(document).ready(function(){uiCheckCtrl()});